mapper.update:
UPDATE REPLY R
   SET REPLY_CONTENT = #{replyContent}
 WHERE REPLY_NO = #{replyNo}
   AND R.STATUS = 'Y'
   AND R.REPLY_WRITER = #{replyWriter}

UPDATE REPLY R
   SET STATUS = 'N'
 WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT COUNT(*)
  FROM APPLICANT
 WHERE REF_ENO = #{refEno}
   AND USER_NO = #{userNo}

SELECT COUNT(*)
  FROM REPLY
 WHERE REF_ENO = #{eventNo}

SELECT COUNT(*)
  FROM APPLICANT
 WHERE REF_ENO = #{eventNo}
