mapper.insert:
INSERT INTO MEMBER(USER_NO
				 , USER_ID
                 , USER_PWD
                 , USER_NAME
                 , EMAIL
                 , BIRTH
                 , GENDER
                 , MARKETING)
            VALUES(SEQ_UNO.NEXTVAL
            	 , #{userId}
                 , #{userPwd}
                 , #{userName}
                 , #{email}
                 , #{birth}
                 , #{gender}
                 , #{marketing})

mapper.select:
SELECT COUNT(*)
  FROM MEMBER
 WHERE USER_ID = #{userId}

mapper.update:
UPDATE MEMBER
   SET USER_PWD = #{userPwd}
 WHERE USER_ID = #{userId}
   AND STATUS = 'Y'

UPDATE MEMBER
   SET USER_NAME = #{userName}
 WHERE USER_ID = #{userId}
   AND STATUS = 'Y'

UPDATE MEMBER
   SET GENDER = #{gender}
 WHERE USER_ID = #{userId}
   AND STATUS = 'Y'

UPDATE MEMBER
   SET BIRTH = #{birth}
 WHERE USER_ID = #{userId}
   AND STATUS = 'Y'

UPDATE MEMBER
   SET MARKETING = #{marketing}
 WHERE USER_ID = #{userId}
   AND STATUS = 'Y'
